import React, { useCallback, useEffect, useState } from "react";
import PropTypes from "prop-types";
import ApiStade from "service/ApiStade"; // tu dois crÃ©er ce service
import TableDropdown from "components/Dropdowns/TableDropdown.js";

export default function GestionDesStades({ color }) {
  const [stades, setStades] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [newStade, setNewStade] = useState({
    name: '',
    location: '',
    capacity: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStade({ ...newStade, [name]: value });
  };

  const getStades = useCallback(async () => {
    try {
      const response = await ApiStade.getAll();
      setStades(response.data.stadeList);
    } catch (error) {
      console.error("Error fetching stades:", error);
    }
  }, []);

  useEffect(() => { getStades(); }, [getStades]);

  const handleAddNewStade = async () => {
    try {
      await ApiStade.add(newStade);
      setIsModalOpen(false);
      getStades();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className={
      "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
      (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
    }>
      <div className="rounded-t mb-0 px-4 py-3 border-0">
        <div className="flex flex-wrap items-center">
          <div className="relative w-full px-4 max-w-full flex-grow flex-1">
            <h3 className={"font-semibold text-lg " + (color === "light" ? "text-blueGray-700" : "text-white")}>
              Gestion des Stades
            </h3>
            <button
              className="ml-2 bg-lightBlue-500 text-white active:bg-lightBlue-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
              type="button"
              onClick={() => setIsModalOpen(true)}
            >
              Add Stade
            </button>

            <hr />

            {isModalOpen && (
              <div>
                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2">Nom</label>
                <input type="text" name="name" value={newStade.name}
                       onChange={handleInputChange}
                       className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-1/4 ease-linear transition-all duration-150" />

                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2">Localisation</label>
                <input type="text" name="location" value={newStade.location}
                       onChange={handleInputChange}
                       className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-1/4 ease-linear transition-all duration-150" />

                <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2">CapacitÃ©</label>
                <input type="number" name="capacity" value={newStade.capacity}
                       onChange={handleInputChange}
                       className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-1/4 ease-linear transition-all duration-150" />

                <button className="ml-2 bg-lightBlue-500 text-white ... " type="button" onClick={handleAddNewStade}>
                  Add Stade
                </button>
                <button className="ml-2 bg-red-400 text-white ..." type="button" onClick={() => setIsModalOpen(false)}>
                  Cancel
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="block w-full overflow-x-auto">
        <table className="items-center w-full bg-transparent border-collapse">
          <thead>
            <tr>
              <th className="px-6 ...">Nom</th>
              <th className="px-6 ...">Localisation</th>
              <th className="px-6 ...">CapacitÃ©</th>
              <th className="px-6 ..."></th>
            </tr>
          </thead>
          <tbody>
            {stades.map((stade) => (
              <tr key={stade._id}>
                <td className="px-6 py-4">{stade.name}</td>
                <td className="px-6 py-4">{stade.location}</td>
                <td className="px-6 py-4">{stade.capacity}</td>
                <td className="px-6 py-4 text-right">
                  <TableDropdown />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

GestionDesStades.defaultProps = { color: "light" };
GestionDesStades.propTypes = { color: PropTypes.oneOf(["light", "dark"]) };
