import React, { useEffect, useState } from "react";
import tournamentsApi from "../../service/";

export default function Tournament() {
  const [tournaments, setTournaments] = useState([]);
  const [name, setName] = useState("");
  const [date, setDate] = useState("");

  // Charger la liste des tournois
  useEffect(() => {
    tournamentsApi.getAll()
      .then((res) => setTournaments(res.data))
      .catch((err) => console.error(err));
  }, []);

  const handleAddTournament = async (e) => {
    e.preventDefault();
    const data = { name, date };
    try {
      await tournamentsApi.add(data);
      // recharger la liste
      const res = await tournamentsApi.getAll();
      setTournaments(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold">Tournois</h1>
      <form onSubmit={handleAddTournament} className="mt-4">
        <input
          type="text"
          placeholder="Nom du tournoi"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="border p-2 mr-2"
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-2 mr-2"
        />
        <button type="submit" className="bg-blue-500 text-white p-2">
          Ajouter
        </button>
      </form>

      <ul className="mt-4">
        {tournaments.map((t) => (
          <li key={t.id}>{t.name} - {t.date}</li>
        ))}
      </ul>
    </div>
  );
}
