import React, { useState } from "react";
impo
const Booking = () => {
  const [bookingData, setBookingData] = useState({
    date: "",
    time: "",
    duration: "1h",
  });

  const handleChange = (e) => {
    setBookingData({
      ...bookingData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Tu peux ajouter une requête API ici
    alert("Réservation envoyée !");
    console.log(bookingData);
  };

  return (
    <div className="p-6 max-w-md mx-auto bg-white rounded-xl shadow-md mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Réserver un terrain</h2>
      <form onSubmit={handleSubmit}>
        <label className="block mb-2">
          Date :
          <input
            type="date"
            name="date"
            value={bookingData.date}
            onChange={handleChange}
            className="mt-1 p-2 w-full border rounded"
            required
          />
        </label>

        <label className="block mb-2">
          Heure :
          <input
            type="time"
            name="time"
            value={bookingData.time}
            onChange={handleChange}
            className="mt-1 p-2 w-full border rounded"
            required
          />
        </label>

        <label className="block mb-2">
          Durée :
          <select
            name="duration"
            value={bookingData.duration}
            onChange={handleChange}
            className="mt-1 p-2 w-full border rounded"
          >
            <option value="1h">1 heure</option>
            <option value="2h">2 heures</option>
            <option value="3h">3 heures</option>
          </select>
        </label>

        <button
          type="submit"
          className="w-full bg-green-600 text-white font-bold py-2 px-4 rounded mt-4"
        >
          Réserver
        </button>
      </form>
    </div>
  );
};

export default Booking;
